import { useEffect } from 'react';
import { useEventListener } from '../../../hooks/useEventListener';
import { usePersistedState } from '../../../hooks/usePersistedState';
import { useResize } from '../../../hooks/useResize';
import { Aside, Ul, Li, Div, TodoCount, Resizer } from './Sidebar.styled';
import { SidebarProps } from './Sidebar.types';

export function Sidebar(props: SidebarProps): JSX.Element {
  const { isSidebarOpen } = props;
  const [persistedWidth, setPersistedWidth] = usePersistedState(
    'sidebar-width',
    '35.1rem'
  );
  const [setRef, width] = useResize({ direction: 'right', max: 600, min: 200 }, s);

  useEffect(() => {
    console.log('----------------------');
    console.log('width final: ', width);
    console.log('persisted with before: ', persistedWidth);
    setPersistedWidth(width);
    return () => {
      console.log('persisted with after: ', persistedWidth);
      console.log('----------------------');
    };
  }, [width]);

  return (
    <Aside
      ref={setRef.element}
      isSidebarOpen={isSidebarOpen}
      widthResizeable={persistedWidth}
    >
      <Resizer ref={setRef.right} />
      <Ul>
        <Li>
          <Div>inbox</Div>
          <TodoCount>2</TodoCount>
        </Li>
        <Li>today</Li>
        <Li>upcoming</Li>
        <Li>filters & labels</Li>
      </Ul>
    </Aside>
  );
}
