import { useCallback, useEffect, useState, useRef } from 'react';

import { useEventListener } from './useEventListener';

type Value<T> = T | null;

type Return<T> = [() => Value<T>, (key: string, value: string) => void];

export function useLocalStorage<T>(): Return<T> {
  const [key, setKey] = useState<string>('');
  const [value, setValue] = useState<string>('');

  const getStoragedValue = (): Value<T> => {
    if (typeof window === 'undefined') {
      return null;
    }

    try {
      const item = window.localStorage.getItem(key);
      return item ? (JSON.parse(item) as T) : null;
    } catch (error) {
      console.warn(`Error reading localStorage key "${key}": `, error);
      return null;
    }
  };

  const setStoragedValue = (storagedKey: string, storagedValue: string) => {
    storagedKey = key;
    window.localStorage.setItem(key, storagedValue);
  };

  return [setKey, setStoragedValue, getStoragedValue];
}
