import { useState, useRef, useLayoutEffect, useCallback } from 'react';
import { nodeModuleNameResolver } from 'typescript';
import { debounce } from '../utils/debounce';

export function useDimensions<T extends HTMLElement>() {
  // setting default values
  const liveMeasure = true;
  const delay = 250;
  const initialDimensions = {};

  const [dimensions, setDimensions] = useState(initialDimensions);
  // const [node, setNode] = useState<T | null>(null);

  // let ref: null | ((newNode: T) => void) = useCallback(
  //   (newNode: T) => {
  //     setNode(newNode);
  //   },
  //   [node]
  // );

  const ref = useRef<T | null>(null);

  useLayoutEffect(() => {
    if (!ref) {
      return;
    }

    const measure = () => {
      window.requestAnimationFrame(() => {
        const newDimensions = ref.current ? ref.current.getBoundingClientRect() : {};
        setDimensions(newDimensions);
      });
    };

    measure();

    if (liveMeasure && ref.current) {
      const debounceMeasure = debounce(measure, delay);

      if ('ResizeObserver' in window) {
        const resizeObserver = new ResizeObserver(debounceMeasure);
        resizeObserver.observe(ref.current);
        window.addEventListener('scroll', debounceMeasure);

        return () => {
          resizeObserver.disconnect();
          window.removeEventListener('scroll', debounceMeasure);
        };
      }
      window.addEventListener('resize', debounceMeasure);
      window.addEventListener('scroll', debounceMeasure);

      return () => {
        window.removeEventListener('resize', debounceMeasure);
        window.addEventListener('scroll', debounceMeasure);
      };
    }
  }, [node, liveMeasure]);

  return [ref, dimensions, node];
}
