import { useContext, createContext } from 'react';
import { ThemeProvider } from 'styled-components';
import { usePersistedState } from '../hooks/usePersistedState';
import { dark, light } from '../styles/theme/theme';
import { ThemeModeContextType, ThemeModeProviderProps } from '../types/types';

const initialValue = {
  mode: 'light',
  toggleMode: () => {},
};

const ThemeModeContext = createContext<ThemeModeContextType>(initialValue);

export const useThemeMode = () => useContext(ThemeModeContext);

export function ThemeModeProvider(props: ThemeModeProviderProps): JSX.Element {
  const [mode, setMode] = usePersistedState<string>('mode', 'light');

  const toggleMode = () => {
    console.log('click');
    setMode(mode === 'light' ? 'dark' : 'light');
  };

  return (
    <ThemeProvider theme={mode === 'light' ? light : dark}>
      <ThemeModeContext.Provider value={{ mode, toggleMode }}>{props.children}</ThemeModeContext.Provider>
    </ThemeProvider>
  );
}
