import styled from 'styled-components';

export type AsideProps = {
  isSidebarOpen: boolean;
};

export const Ul = styled.ul``;

export const Li = styled.li`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

export const Div = styled.div`
  display: flex;
  align-items: center;
`;

export const TodoCount = styled.span`
  color: ${(props) => props.theme.colors.grey.three};
  font-size: 1.4rem;
  justify-self: flex-end;
`;

export const Aside = styled.aside<AsideProps>`
  position: fixed;
  width: 30rem;
  min-height: 100%;
  background-color: ${(props) => props.theme.colors.white.two};

  margin-left: ${(props) => (props.isSidebarOpen ? '0' : '-30.1rem')};

  padding: 8rem 1rem 4rem 8rem;

  transition: margin-left 0.125s ease;

  ul {
    li:not(:last-child) {
      margin-bottom: 2rem;
    }
  }
`;
