import { useEffect, useMemo, useState } from 'react';
import { BasePageProps } from './BasePage.types';

import { Navbar } from '../../components/layout/Navbar/Navbar.component';
import { Sidebar } from '../../components/layout/Sidebar/Sidebar.component';
import { Content } from './BasePage.styled';
import { useWindowSize } from '../../hooks/useWindowSize';
import { breakpoints } from '../../styles/theme/theme';

export function BasePage(props: BasePageProps): JSX.Element {
  const windowSize = useWindowSize();

  const checkWindowWidth = useMemo(() => {
    if (windowSize.width && windowSize.width > parseInt(breakpoints.md, 10)) {
      return false;
    } else if (windowSize.width && windowSize.width < parseInt(breakpoints.md, 10)) {
      return true;
    } else {
      return false;
    }
  }, [windowSize.width]);

  const [isSidebarOpen, setIsSidebarOpen] = useState<boolean>(true);
  const [isBreakpointMd, setIsBreakpointMd] = useState<boolean | undefined>(
    undefined
  );

  useEffect(() => {
    setIsBreakpointMd(checkWindowWidth);
  }, [windowSize.width]);

  const toggleSidebar = () => setIsSidebarOpen((state) => !state);

  const closeSidebar = () => setIsSidebarOpen(false);
  const openSidebar = () => setIsSidebarOpen(true);

  return (
    <>
      <Navbar toggleSidebar={toggleSidebar} isSidebarOpen={isSidebarOpen} />
      <Sidebar
        closeSidebar={closeSidebar}
        openSidebar={openSidebar}
        isSidebarOpen={isSidebarOpen}
        isBreakpointMd={isBreakpointMd}
        setIsBreakpointMd={setIsBreakpointMd}
      />
      <Content isSidebarOpen={isSidebarOpen}></Content>
    </>
  );
}
