import { useContext, createContext } from 'react';
import { DefaultTheme } from 'styled-components';
import { usePersistedState } from '../hooks/usePersistedState';
import { light, dark } from '../styles/theme/theme';
import { AppThemeContextType, AppThemeProviderProps } from '../types/types';

const initialValue = {

}

const AppThemeContext = createContext<AppThemeContextType>({});

export const useAppTheme = () => useContext(AppThemeContext);

export function AppThemeProvider(props : AppThemeProviderProps): JSX.Element {
  const [theme, setTheme] = usePersistedState<DefaultTheme>(light);

  const toggleTheme = () => {
    setTheme(theme === light ? dark : light);
  };

  return (
    <AppThemeContext.Provider value={{theme, toggleTheme}}>{props.children}</AppThemeContext.Provider>
  )
}
