import React, { useState, useRef, useLayoutEffect, useCallback } from 'react';
import { setOriginalNode } from 'typescript';

export function useDimensions<T extends HTMLElement>(
  liveMeasure = true,
  delay = 250,
  initialDimensions = {},
  effectDeps = []
) {
  const [dimensions, setDimensions] = useState(initialDimensions);
  const [node, setNode] = useState<T>(null);

  const ref = useCallback((newNode: T) => {
    setNode(newNode);
  }, []);

  useLayoutEffect(() => {
    if (!node) {
      return;
    }

    const measure = () => {
      window.requestAnimationFrameFrame(() => {
        const newDimensions = node.getBoundingClientRect();
        setDimensions(newDimensions);
      });
    };
  });

  const _ = useRef<T>(null);

  useLayoutEffect(() => {
    if (ref.current) setDimensions(ref.current.getBoundingClientRect());
  }, [ref.current]);

  return [ref, dimensions];
}
