import styled from 'styled-components';

export const Div = styled.div`
  background-color: ${(props) => props.theme.colors.grey.one};
  border: none;
  border-radius: 4px;
  padding: 0.4rem 0.6rem;
  position: absolute;
  z-index: 3;

  bottom: -3.9rem;
  left: -50%;
  transform: translateX(50%);
`;
