import { useLayoutEffect, useRef } from 'react';

interface Limit {
  min: number;
  max: number;
}

export function useResize<T extends HTMLElement>() {
  const refElement = useRef<T>();
  const refTop = useRef<T>();
  const refRight = useRef<T>();
  const refBottom = useRef<T>();
  const refLeft = useRef<T>();

  useLayoutEffect(() => {
    if (refElement && refElement.current) {
      const resizeableElement = refElement.current;
      const styles = window.getComputedStyle(resizeableElement as T);
      let width = parseInt(styles.width, 10);
      let heihgt = parseInt(styles.height, 10);
      let x = 0;
      let y = 0;

      // Right resize
      const onMouseMoveRightResize = (event: MouseEvent, limit?: Limit) => {
        // testing foo working
        console.log('move');

        const dx = event.clientX - x;
        x = event.clientX;
        width = width + dx;
        resizeableElement.style.width = `${width}px`;
      };

      const onMouseUpRightResize = (event: MouseEvent) => {
        document.removeEventListener('mousemove', onMouseMoveRightResize);
      };

      const onMouseDownRightResize = (event: MouseEvent) => {
        x = event.clientX;
        resizeableElement.style.left = styles.left;
        resizeableElement.style.right = 'auto';
        document.addEventListener('mousemove', onMouseMoveRightResize);
        document.addEventListener('mouseup', onMouseUpRightResize);
      };

      const resizerRight = refRight.current;
      resizerRight?.addEventListener('mousedown', onMouseDownRightResize);

      return () => {
        resizerRight?.removeEventListener('mousedown', onMouseDownRightResize);
      };
    }
  });

  const ref = {
    element: refElement,
    right: refRight,
  };

  return { ref };
}
