import { useResize } from '../../../hooks/useResize';
import { Aside, Ul, Li, Div, TodoCount, Resizer } from './Sidebar.styled';
import { SidebarProps } from './Sidebar.types';

export function Sidebar(props: SidebarProps): JSX.Element {
  const { isSidebarOpen } = props;
  const [ref, storagedWidth] = useResize({
    right: { width: { min: 250, max: 500 } },
  });

  console.log(localStorage);

  return (
    <Aside
      ref={ref.element}
      isSidebarOpen={isSidebarOpen}
      storagedWidth={storagedWidth}
    >
      <Resizer ref={ref.right} />
      <Ul>
        <Li>
          <Div>inbox</Div>
          <TodoCount>2</TodoCount>
        </Li>
        <Li>today</Li>
        <Li>upcoming</Li>
        <Li>filters & labels</Li>
      </Ul>
    </Aside>
  );
}
