import { useLayoutEffect, useEffect, useRef, useState } from 'react';
import { usePersistedState } from './usePersistedState';

interface SizeLimit {
  min?: number;
  max?: number;
}

interface UseResizeProps {
  limits: {
    top?: SizeLimit;
    right?: SizeLimit;
    bottom?: SizeLimit;
    left?: SizeLimit;
  };
}

type UseResizeResponse<T> = {
  refs: {
    element: React.Dispatch<React.SetStateAction<T | null>>;
    top: React.Dispatch<React.SetStateAction<T | null>>;
    right: React.Dispatch<React.SetStateAction<T | null>>;
    bottom: React.Dispatch<React.SetStateAction<T | null>>;
    left: React.Dispatch<React.SetStateAction<T | null>>;
  };
};

export function useResize<T extends HTMLElement>(
  props?: UseResizeProps
): [UseResizeResponse<T>] {
  const [element, setElement] = useState<T | null>(null);
  const [top, setTop] = useState<T | null>(null);
  const [right, setRight] = useState<T | null>(null);
  const [bottom, setBottom] = useState<T | null>(null);
  const [left, setLeft] = useState<T | null>(null);

  let widthPx: string = '';
  let heightPx: string = '';

  const elementSizes = {
    width: widthPx,
  };

  const limits = props ? props.limits : undefined;

  const elementIdByClassName = element?.className;

  console.log(elementIdByClassName);

  console.log(element, top, right, bottom, left);

  useLayoutEffect(() => {
    if (element && (top || right || bottom || left)) {
      console.log('use layout effect');

      const resizeableElement = element;
      const styles = window.getComputedStyle(resizeableElement);
      let width = parseInt(styles.width, 10);
      let height = parseInt(styles.height, 10);
      let x = 0;
      let y = 0;

      // Right resize
      const onMouseMoveRightResize = (event: MouseEvent) => {
        // testing foo working
        console.log('move');

        const dx = event.clientX - x;
        x = event.clientX;
        width = width + dx;
        widthPx = `${width}px`;

        // check to set width when has or not limit
        if (!props) {
          resizeableElement.style.width = widthPx;
        } else if (limits && limits.right) {
          if (limits.right.max && limits.right.min) {
            if (width > limits.right.min && width < limits.right.max) {
              resizeableElement.style.width = widthPx;
            }
          } else if (limits.right.max) {
            if (width < limits.right.max) {
              resizeableElement.style.width = widthPx;
            }
          } else if (limits.right.min) {
            if (width > limits.right.min) {
              resizeableElement.style.width = widthPx;
            }
          }
        }

        if (elementIdByClassName) {
          console.log('local storage');
          localStorage.setItem(elementIdByClassName, widthPx);
        }
      };

      const onMouseUpRightResize = (event: MouseEvent) => {
        document.removeEventListener('mousemove', onMouseMoveRightResize);
      };

      const onMouseDownRightResize = (event: MouseEvent) => {
        x = event.clientX;
        resizeableElement.style.left = styles.left;
        resizeableElement.style.right = 'auto';
        document.addEventListener('mousemove', onMouseMoveRightResize);
        document.addEventListener('mouseup', onMouseUpRightResize);
      };

      const resizerRight = right;
      resizerRight?.addEventListener('mousedown', onMouseDownRightResize);

      return () => {
        resizerRight?.removeEventListener('mousedown', onMouseDownRightResize);
      };
    }
  }, [element, top, right, bottom, left]);

  const refs = {
    element: setElement,
    top: setTop,
    right: setRight,
    bottom: setBottom,
    left: setLeft,
  };

  return [refs];
}
